set emp_zip =#{emp_zip}, emp_addr=#{emp_addr}, emp_daddr=#{emp_daddr}
where emp_no=#{emp_no}

update EMP_INFO 
set dlng_bank_nm =#{dlng_bank_nm}, dlng_actno=#{dlng_actno}
where emp_no=#{emp_no}
